import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const imgUrls = ["https://sophosnews.files.wordpress.com/2019/01/shutterstock_719314501-1-compressor.jpg?w=780&h=408&crop=1",
"https://www.it.miami.edu/_assets/images/adobecc.png",
"https://www.bleepstatic.com/images/news/u/986406/Logos/Adobe_Logo.png"];

class Carousel extends React.Component {
	constructor (props) {
    super(props);

    this.state = {
      currentImageIndex: 0
    };
    this.nextSlide = this.nextSlide.bind(this);
    this.previousSlide = this.previousSlide.bind(this);
  }

  cropIndex(i) {
	if(i >= imgUrls.length){
		return 0;
	}else if(i < 0){
		return imgUrls.length-1;
	}

	return i;
  }

  previousSlide() {
	const prevIndex = this.cropIndex((this.state.currentImageIndex - 1));
	this.setState({currentImageIndex: prevIndex,});
  }

  nextSlide() {
	const nextIndex = this.cropIndex((this.state.currentImageIndex + 1));
	this.setState({currentImageIndex: nextIndex,});
  }

  render () {
    return (
      <div className="carousel">
        <Arrow
          direction="left"
          clickFunction={ this.previousSlide }
          glyph="&#9664;" />

        <ImageSlide url= {imgUrls[this.state.currentImageIndex]} true   />

        <Arrow
          direction="right"
          clickFunction={ this.nextSlide }
          glyph="&#9654;" />
      </div>
    );
  }
}

class Video extends React.Component {

    render () {
        return (
            <video id="background-video" loop autoPlay>
                <source src={'https://www.sample-videos.com/video123/mp4/720/big_buck_bunny_720p_1mb.mp4'} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
        )
    }
};

const ImageSlide = ({ url, image }) => {
	if(!{image}){
		const styles = {
		backgroundImage: `url(${url})`,
		backgroundSize: 'cover',
		backgroundPosition: 'center',
	  };

	  return (
		<div className="image-slide" style={styles}>
		</div>
	  );

	}else{
		return <h1> 'tutu' </h1>;
	}

}

const Arrow = ({ direction, clickFunction, glyph }) => (
  <div
    className={ `slide-arrow ${direction}` }
    onClick={ clickFunction }>
    { glyph }
  </div>
);

ReactDOM.render(
  <Carousel />,
  document.getElementById('root')
);
