import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const imgUrls = ['https://www.sample-videos.com/video123/mp4/720/big_buck_bunny_720p_1mb.mp4'];
const imgs = [true];

class Carousel extends React.Component {
	constructor (props) {
    super(props);

    this.state = {
      currentImageIndex: 0
    };
    this.nextSlide = this.nextSlide.bind(this);
    this.previousSlide = this.previousSlide.bind(this);
  }

  cropIndex(i) {
	if(i >= imgUrls.length){
		return 0;
	}else if(i < 0){
		return imgUrls.length-1;
	}

	return i;
  }

  previousSlide() {
	const prevIndex = this.cropIndex((this.state.currentImageIndex - 1));
	this.setState({currentImageIndex: prevIndex,});
  }

  nextSlide() {
	const nextIndex = this.cropIndex((this.state.currentImageIndex + 1));
	this.setState({currentImageIndex: nextIndex,});
  }

  render () {
	const isImg = imgs[this.state.currentImageIndex];
    return (
      <div className="carousel">
        <Arrow
          direction="left"
          clickFunction={ this.previousSlide }
          glyph="&#9664;" />

        <ImageSlide image={imgs[this.state.currentImageIndex] } url= {imgUrls[this.state.currentImageIndex]}   />

        <Arrow
          direction="right"
          clickFunction={ this.nextSlide }
          glyph="&#9654;" />
      </div>
    );
  }
}


const ImageSlide = ({ url, image }) => {
	if({image}==true){
		const styles = {
		backgroundImage: `url(${url})`,
		backgroundSize: 'cover',
		backgroundPosition: 'center',
	  };

	  return (
		<div className="image-slide" style={styles}>
		</div>
	  );
  }
	return (
            <video id="video" loop autoPlay>
                <source src={url} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
        )

}

const Arrow = ({ direction, clickFunction, glyph }) => (
  <div
    className={ `slide-arrow ${direction}` }
    onClick={ clickFunction }>
    { glyph }
  </div>
);

ReactDOM.render(
  <Carousel />,
  document.getElementById('root')
);
